package loops.project;

public class while_activity {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 int n=5;
		 int i=0;
		 while(i<5)
		 {
			 int j=0;
			 while(j<=i)
			 {
				 System.out.print("*");
				 j++;
		
			 }
			 System.out.println();
			 i++;
		 }
		 

	}

}
